import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  // Close dropdown when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isDropdownOpen && !event.target.closest('.navbar-dropdown')) {
        setIsDropdownOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isDropdownOpen]);

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/" className="navbar-logo">
          Virtual Betting
        </Link>
      </div>
      <div className="navbar-menu">
        <Link to="/" className="navbar-item">Home</Link>
        
        {/* Dropdown Menu */}
        <div className="navbar-dropdown">
          <button 
            className="navbar-dropdown-btn"
            onClick={toggleDropdown}
          >
            Games â–¼
          </button>
          {isDropdownOpen && (
            <div className="dropdown-menu">
              <Link to="/matches" className="dropdown-item">Upcoming Events</Link>
              <Link to="/results" className="dropdown-item">Recent Results</Link>
            </div>
          )}
        </div>
        
        <Link to="/bet-slip" className="navbar-item">Bet Slip</Link>
        <Link to="/login" className="navbar-item">Login</Link>
      </div>
    </nav>
  );
}

export default Navbar;
