import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import './Aviator.css';

// Set the base URL for API calls (env override, default to local backend)
const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://127.0.0.1:8000';

// Debug logging helper - only logs in development
const debugLog = (...args) => {
  if (process.env.NODE_ENV === 'development') {
    console.log(...args);
  }
};

const Aviator = () => {
  const [gameState, setGameState] = useState('waiting');
  const [multiplier, setMultiplier] = useState(1.00);
  const [betAmount, setBetAmount] = useState(10);
  const [balance, setBalance] = useState(1000);
  const [isBetPlaced, setIsBetPlaced] = useState(false);
  const [gameHistory, setGameHistory] = useState([]);
  const [timeLeft, setTimeLeft] = useState(5);
  const [crashPoint, setCrashPoint] = useState(0);
  const [roundNumber, setRoundNumber] = useState(0);
  const [betHistory, setBetHistory] = useState([]);
  const [currentGame, setCurrentGame] = useState(null);
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isCashingOut, setIsCashingOut] = useState(false);
  const [hasLocalActiveBet, setHasLocalActiveBet] = useState(false);
  const [activeBets, setActiveBets] = useState([]);
  const [cooldownTime, setCooldownTime] = useState(0);
  const [isCooldownActive, setIsCooldownActive] = useState(false);

  const gameInterval = useRef(null);
  const gameCheckInterval = useRef(null);
  const gameTimeout = useRef(null);
  const cooldownInterval = useRef(null);
  const isRunningRef = useRef(false);
  const lastUpdateTime = useRef(0);
  const isBetPlacedRef = useRef(false);
  const hasLocalActiveBetRef = useRef(false);

  // API calls
  const fetchBalance = async () => {
    try {
      debugLog('Fetching balance from:', `${API_BASE_URL}/api/aviator/balance/`);
      const response = await axios.get(`${API_BASE_URL}/api/aviator/balance/?user_id=player_1`);
      debugLog('Balance response:', response.data);
      setBalance(response.data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
      console.error('Error details:', error.response?.data);
      setBalance(1000);
    }
  };

  const fetchCurrentGame = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/aviator/realtime/`);
      const gameData = response.data;
      const backendStatus = gameData.status; // 'waiting' | 'active' | 'crashed'
      const effectivePhase = gameData.phase || (backendStatus === 'active' ? 'playing' : backendStatus);

      // Debug logging
      debugLog('Backend data:', {
        status: backendStatus,
        phase: gameData.phase,
        effectivePhase,
        time_remaining: gameData.time_remaining,
        round_number: gameData.round_number,
        current_multiplier: gameData.current_multiplier
      });

      // Check if this is a new round
      const isNewRound = gameData.round_number !== roundNumber;

      // Only update state if there are actual changes to prevent unnecessary re-renders
      if (gameData.round_number !== roundNumber) {
        setRoundNumber(gameData.round_number);
      }

      if (Number(gameData.crash_point) !== crashPoint) {
        setCrashPoint(Number(gameData.crash_point) || 1.5);
      }

      // Batch state updates to prevent multiple re-renders
      const stateUpdates = {};

      // Update game state only if it actually changed
      if (effectivePhase === 'waiting') {
        // Transition to waiting state (backend controls the timing)
        if (gameState !== 'waiting') {
          debugLog('Transitioning to waiting state, time_remaining:', gameData.time_remaining);
          stateUpdates.gameState = 'waiting';
        }
        stateUpdates.multiplier = 1.00;

        if (typeof gameData.time_remaining === 'number') {
          const newTimeLeft = Math.max(0, Math.ceil(gameData.time_remaining));
          if (newTimeLeft !== timeLeft) {
            debugLog('Updating countdown:', timeLeft, '->', newTimeLeft);
            stateUpdates.timeLeft = newTimeLeft;
          }
        }

        if (isNewRound) {
          stateUpdates.isBetPlaced = false;
          stateUpdates.hasLocalActiveBet = false;
        }
      } else if ((effectivePhase === 'playing' || effectivePhase === 'starting') && gameState !== 'playing') {
        stateUpdates.gameState = 'playing';
        if (typeof gameData.current_multiplier === 'number') {
          stateUpdates.multiplier = gameData.current_multiplier;
        }
        if (typeof gameData.time_remaining === 'number') {
          stateUpdates.timeLeft = Math.max(0, Math.ceil(gameData.time_remaining));
        }
      } else if ((gameData.game_crashed || effectivePhase === 'crashed') && gameState !== 'crashed') {
        stateUpdates.gameState = 'crashed';
        stateUpdates.multiplier = Number(gameData.crashed_at) || Number(gameData.crash_point) || 1.5;
        stateUpdates.isBetPlaced = false;
        stateUpdates.hasLocalActiveBet = false;
      }

      // Apply all state updates at once
      Object.entries(stateUpdates).forEach(([key, value]) => {
        switch (key) {
          case 'gameState':
            setGameState(value);
            break;
          case 'multiplier':
            setMultiplier(value);
            break;
          case 'timeLeft':
            setTimeLeft(value);
            break;
          case 'isBetPlaced':
            setIsBetPlaced(value);
            break;
          case 'hasLocalActiveBet':
            setHasLocalActiveBet(value);
            break;
          default:
            // No action needed for unknown keys
            break;
        }
      });

      // Handle async operations after state updates
      if (effectivePhase === 'waiting' && isNewRound) {
        await checkActiveBet();
      } else if ((effectivePhase === 'playing' || effectivePhase === 'starting') && gameState !== 'playing') {
        await checkActiveBet();
      }

      // Update multiplier and time only during active gameplay to prevent flickering
      if (stateUpdates.gameState === 'playing' || effectivePhase === 'playing') {
        const now = Date.now();
        // Debounce updates to prevent rapid re-renders
        if (now - lastUpdateTime.current > 100) {
          // Only update if multiplier actually changed to prevent unnecessary re-renders
          if (typeof gameData.current_multiplier === 'number' && Math.abs(gameData.current_multiplier - multiplier) > 0.001) {
            setMultiplier(gameData.current_multiplier);
          }
          if (typeof gameData.time_remaining === 'number' && Math.abs(gameData.time_remaining - timeLeft) > 0.1) {
            setTimeLeft(Math.max(0, Math.ceil(gameData.time_remaining)));
          }
          lastUpdateTime.current = now;
        }
      }

      return gameData;
    } catch (error) {
      console.error('Error fetching real-time game state:', error);
      return null;
    }
  };

  const fetchGameHistory = async () => {
    try {
      debugLog('Fetching game history from:', `${API_BASE_URL}/api/aviator/game-history/`);
      const response = await axios.get(`${API_BASE_URL}/api/aviator/game-history/`);
      debugLog('Game history response:', response.data);
      debugLog('Game history response type:', typeof response.data);
      debugLog('Game history response length:', response.data?.length);

      // Ensure we extract numeric crash points and filter out invalid values
      const crashPoints = response.data
        .map(game => Number(game.crash_point))
        .filter(point => !isNaN(point) && point > 0);

      debugLog('Processed crash points:', crashPoints);
      setGameHistory(crashPoints);
    } catch (error) {
      console.error('Error fetching game history:', error);
      console.error('Error details:', error.response?.data);
      // Set empty array as fallback
      setGameHistory([]);
    }
  };

  const fetchBetHistory = async () => {
    try {
      debugLog('Fetching bet history from:', `${API_BASE_URL}/api/aviator/history/`);
      const response = await axios.get(`${API_BASE_URL}/api/aviator/history/`);
      debugLog('Bet history response:', response.data);
      setBetHistory(response.data);
    } catch (error) {
      console.error('Error fetching bet history:', error);
      console.error('Error details:', error.response?.data);
    }
  };

  // Check if user has an active bet in the current round - simplified to prevent blinking
  const checkActiveBet = async () => {
    // Only check on new rounds or when explicitly needed
    if (gameState === 'playing' && hasLocalActiveBetRef.current) {
      // Don't check during active gameplay to prevent button blinking
      return true;
    }

    try {
      const response = await axios.get(`${API_BASE_URL}/api/aviator/check-active-bet/?user_id=player_1&round_number=${roundNumber}`);
      const hasActiveBet = response.data.has_active_bet;
      setIsBetPlaced(hasActiveBet);
      if (hasActiveBet) {
        setHasLocalActiveBet(true);
      } else if (gameState !== 'playing') {
        setHasLocalActiveBet(false);
      }
      return hasActiveBet;
    } catch (error) {
      console.error('Error checking active bet:', error);
      // Don't change state on errors during gameplay
      if (gameState !== 'playing') {
        setIsBetPlaced(false);
        setHasLocalActiveBet(false);
        return false;
      }
      return hasLocalActiveBetRef.current;
    }
  };

  // Place bet - only allowed during waiting phase
  const placeBet = async () => {
    // Validate bet placement conditions
    if (gameState !== 'waiting') {
      setError('Bets can only be placed during waiting phase');
      return;
    }

    if (isBetPlaced) {
      setError('You already have an active bet in this round');
      return;
    }

    if (balance < betAmount) {
      setError('Insufficient balance');
      return;
    }

    try {
      const response = await axios.post(`${API_BASE_URL}/api/aviator/place-bet/`, {
        amount: betAmount,
        user_id: 'player_1'  // Simple user ID for anonymous betting
      });

      setBalance(response.data.balance);
      setIsBetPlaced(true);
      setHasLocalActiveBet(true);
      setError(null);
      if (response.data?.bet?.id) {
        setActiveBets((prev) => [{ id: response.data.bet.id, amount: Number(response.data.bet.amount) }, ...prev]);
      }
      else {
        // Fallback: refresh slips from backend
        try {
          const r = await axios.get(`${API_BASE_URL}/api/aviator/active-bets/?user_id=player_1&round_number=${roundNumber}`);
          const list = (r.data?.bets || []).map(b => ({ id: b.id, amount: Number(b.amount) }));
          setActiveBets(list);
        } catch (_) {}
      }

      // Verify the bet was placed successfully
      const hasActiveBet = await checkActiveBet();
      if (!hasActiveBet) {
        setIsBetPlaced(false);
      }
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to place bet');
      console.error('Error placing bet:', error);
      // Ensure bet state is false if placement failed
      setIsBetPlaced(false);
      setHasLocalActiveBet(false);
    }
  };

  // Cashout - only allowed when bet is placed and game is active
  const cashout = async (specificBetId = null) => {
    // Prevent multiple rapid clicks
    if (isCashingOut) {
      return;
    }

    // Validate cashout conditions
    if (!isBetPlaced && activeBets.length === 0) {
      setError('No active bet to cashout');
      return;
    }

    if (gameState !== 'playing') {
      setError('Can only cashout during active gameplay');
      return;
    }

    setIsCashingOut(true);

    try {
      const response = await axios.post(`${API_BASE_URL}/api/aviator/cashout/`, {
        round_number: roundNumber,
        multiplier: multiplier,
        user_id: 'player_1',  // Simple user ID for anonymous betting
        bet_id: specificBetId
      });

      setBalance(response.data.balance);
      if (response.data?.bet?.id) {
        setActiveBets((prev) => prev.filter(b => b.id !== response.data.bet.id));
      }
      const remaining = activeBets.length - 1;
      setIsBetPlaced(remaining > 0);
      setHasLocalActiveBet(remaining > 0);
      setBetHistory(prev => [response.data.bet, ...prev.slice(0, 19)]);
      setError(null);

      // Refresh balance to ensure it's up to date
      await fetchBalance();

      // Verify bet state was updated
      await checkActiveBet();
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to cashout');
      console.error('Error cashing out:', error);
    } finally {
      setIsCashingOut(false);
    }
  };


  // Bet controls
  const doubleBet = () => {
    if (balance >= betAmount * 2) {
      setBetAmount(prev => prev * 2);
    }
  };

  const halfBet = () => {
    setBetAmount(prev => Math.max(1, Math.floor(prev / 2)));
  };

  const resetBet = () => {
    setBetAmount(10);
  };

  const addChip = (amount) => {
    setBetAmount(prev => {
      const next = Math.max(1, prev + amount);
      return Math.min(next, balance);
    });
  };

  const setMaxBet = () => {
    setBetAmount(Math.max(1, Math.floor(balance)));
  };

  const clearBet = () => {
    setBetAmount(1);
  };


  // Handle game crash
  const handleGameCrash = async () => {
    // Reset bet state when game crashes
    setIsBetPlaced(false);
    setHasLocalActiveBet(false);

    // Update bet history if there was an active bet
    if (isBetPlaced) {
      await fetchBetHistory();
    }

    // Start cooldown countdown (3 seconds)
    startCooldownCountdown(3);
  };

  // Start cooldown countdown
  const startCooldownCountdown = (seconds) => {
    setIsCooldownActive(true);
    setCooldownTime(seconds);

    // Clear any existing cooldown timer
    if (cooldownInterval.current) {
      clearInterval(cooldownInterval.current);
    }

    cooldownInterval.current = setInterval(() => {
      setCooldownTime(prev => {
        if (prev <= 1) {
          // Cooldown finished, clear interval
          if (cooldownInterval.current) {
            clearInterval(cooldownInterval.current);
            cooldownInterval.current = null;
          }
          setIsCooldownActive(false);
          // Let backend polling handle the state transition
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
  };

  // Effects
  useEffect(() => {
    if (gameState === 'crashed') {
      // Handle game crash - backend polling will handle the next round automatically
      handleGameCrash();
    }
  }, [gameState]);



  // Only check bet state when game becomes active, not during gameplay
  useEffect(() => {
    if (gameState === 'playing') {
      // Check bet state only once when game becomes active
      const immediateCheck = async () => {
        await checkActiveBet();
      };
      immediateCheck();
    }
  }, [gameState]);

  // Keep a ref in sync with the state to make race-free decisions during polling
  useEffect(() => {
    isBetPlacedRef.current = isBetPlaced;
  }, [isBetPlaced]);

  useEffect(() => {
    hasLocalActiveBetRef.current = hasLocalActiveBet;
  }, [hasLocalActiveBet]);

  useEffect(() => {
    return () => {
      if (gameInterval.current) clearInterval(gameInterval.current);
      if (gameCheckInterval.current) clearInterval(gameCheckInterval.current);
      if (cooldownInterval.current) clearInterval(cooldownInterval.current);
      if (gameTimeout.current) clearTimeout(gameTimeout.current);
    };
  }, []);

  // Initialize and periodic updates
  useEffect(() => {
    debugLog('Aviator component mounted - starting initialization');
    setIsLoading(true);

    const initializeComponent = async () => {
      try {
        debugLog('Starting API calls...');
        await Promise.all([
          fetchBalance(),
          fetchCurrentGame().catch(() => undefined),
          fetchGameHistory(),
          fetchBetHistory()
        ]);

        // Check for active bet after getting current game
        if (roundNumber > 0) {
          await checkActiveBet();
        }

        debugLog('All API calls completed - server connected');
      } catch (error) {
        console.error('Error during initialization:', error);
      } finally {
        setIsLoading(false);
        debugLog('Component initialization complete - ready for countdown');
      }
    };

    initializeComponent();

    // Poll backend for real-time updates - this will handle countdown automatically
    if (gameCheckInterval.current) clearInterval(gameCheckInterval.current);
    // Poll every 500ms for optimal performance and real-time updates
    gameCheckInterval.current = setInterval(() => {
      // Poll to get latest real-time state from backend (including countdown)
      debugLog('Polling backend for updates...');
      fetchCurrentGame().catch((error) => {
        console.error('Polling error:', error);
      });
    }, 500);

    return () => {
      debugLog('Cleaning up Aviator component');
      if (gameCheckInterval.current) clearInterval(gameCheckInterval.current);
    };
  }, []); // Removed gameState dependency

  debugLog('Rendering Aviator component, gameState:', gameState, 'timeLeft:', timeLeft, 'isLoading:', isLoading, 'isCooldownActive:', isCooldownActive, 'cooldownTime:', cooldownTime);

  if (isLoading) {
    return (
      <div className="aviator-container">
        <div className="loading-state">
          <h2>Loading Aviator Game...</h2>
          <div className="loading-spinner"></div>
          <p>Connecting to backend...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="aviator-container">
      <div className="aviator-header">
        <h1>Aviator</h1>
        <div className="balance-display">
          Balance: ${balance.toFixed(2)}
        </div>
      </div>

      {error && (
        <div className="error-message">
          <strong>Error:</strong> {error}
          <button onClick={() => setError(null)} className="error-close"></button>
        </div>
      )}

      <div className="debug-info">
        <small>API Base: {API_BASE_URL}</small>
        <small>Game State: {gameState}</small>
        <small>Round: #{roundNumber}</small>
        <small>Game History Count: {gameHistory.length}</small>
        <small>Bet History Count: {betHistory.length}</small>
        <small>Current Game: {currentGame ? `Round ${currentGame.round_number}` : 'None'}</small>
      </div>

      <div className="game-area">
        <div className="multiplier-display">
          <AnimatePresence mode="wait">
            {isCooldownActive && (
              <motion.div
                key="cooldown"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="cooldown-state"
              >
                <div className="game-display">
                  <div className="aviator-plane-container">
                    <div className={`aviator-plane cooldown`}>

                    </div>
                  </div>
                  <h2>Cooldown: {cooldownTime}s</h2>
                  <div className="countdown-circle cooldown">
                    <span className="countdown-number">{cooldownTime}</span>
                  </div>
                  <div className="cooldown-message">Preparing next round...</div>
                  <div className="round-info">Round #{roundNumber + 1}</div>
                </div>
              </motion.div>
            )}

            {gameState === 'waiting' && !isCooldownActive && (
              <motion.div
                key="waiting"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="waiting-state"
              >
                <div className="game-display">
                  <div className="aviator-plane-container">
                    <div className={`aviator-plane waiting`}>

                    </div>
                  </div>
                  <h2>Next game in {timeLeft}s</h2>
                  <div className="countdown-circle">
                    <span className="countdown-number">{timeLeft}</span>
                  </div>
                  <div className="round-info">Round #{roundNumber}</div>
                </div>
              </motion.div>
            )}

            {gameState === 'playing' && (
              <motion.div
                key="playing"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="playing-state"
              >
                <div className="game-display">
                  <div className="aviator-plane-container">
                    <div className={`aviator-plane flying`}>

                    </div>
                  </div>
                  <div className="multiplier-value">
                    {multiplier.toFixed(2)}x
                  </div>
                  <div className="game-status">Flying!</div>
                  {Number(crashPoint) > 0 && (
                    <div className="crash-point">
                      Target: {(Number(crashPoint) || 1.0).toFixed(2)}x
                    </div>
                  )}
                  <div className="round-info">Round #{roundNumber}</div>
                </div>
              </motion.div>
            )}

            {gameState === 'crashed' && (
              <motion.div
                key="crashed"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="crashed-state"
              >
                <div className="game-display">
                  <div className="aviator-plane-container">
                    <div className={`aviator-plane crashed`}>

                    </div>
                  </div>
                  <h2 className="crash-text">CRASHED AT {(Number(crashPoint) || 1.0).toFixed(2)}x</h2>
                  <div className="game-status">Game Over!</div>
                  <div className="round-info">Round #{roundNumber}</div>
                  <div className="backend-info">
                    <small>Predetermined crash point from backend</small>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        <div className="betting-controls">
          <div className="bet-amount-controls">
            <button onClick={halfBet} className="bet-btn"></button>
            <button onClick={resetBet} className="bet-btn">Reset</button>
            <button onClick={doubleBet} className="bet-btn">2</button>
          </div>

          <div className="bet-input">
            <input
              type="number"
              value={betAmount}
              onChange={(e) => setBetAmount(Math.max(1, parseInt(e.target.value) || 1))}
              min="1"
              max={balance}
              className="bet-amount-input"
            />
          </div>

          <div className="quick-chips">
            <button className="chip" onClick={() => addChip(1)}>+1</button>
            <button className="chip" onClick={() => addChip(5)}>+5</button>
            <button className="chip" onClick={() => addChip(10)}>+10</button>
            <button className="chip" onClick={() => addChip(50)}>+50</button>
            <button className="chip" onClick={() => addChip(100)}>+100</button>
            <button className="chip outline" onClick={setMaxBet}>Max</button>
            <button className="chip outline" onClick={clearBet}>Clear</button>
          </div>

          <div className="action-buttons">
            {/* Bet button - only shown during waiting phase when no bet is placed and not in cooldown */}
            {!hasLocalActiveBet && gameState === 'waiting' && !isCooldownActive && (
              <button onClick={placeBet} className="place-bet-btn">
                Place Bet ${betAmount}
              </button>
            )}

            {/* Cashout button - persists from bet placement until crash/cashout */}
            {hasLocalActiveBet && gameState !== 'crashed' && (
              <button 
                onClick={cashout} 
                className="cashout-btn"
                disabled={gameState !== 'playing' || isCashingOut}
                style={{ pointerEvents: 'auto' }}
              >
                {gameState !== 'playing'
                  ? 'Cashout (waiting for takeoff)'
                  : isCashingOut
                    ? 'Cashing Out...'
                    : `Cashout $${(betAmount * multiplier).toFixed(2)}`}
              </button>
            )}

            {/* Disabled state - when no bet is placed and game is not in waiting phase */}
            {!hasLocalActiveBet && (gameState !== 'waiting' || isCooldownActive) && (
              <button disabled className="disabled-btn">
                {isCooldownActive 
                  ? `Cooldown: ${cooldownTime}s remaining`
                  : gameState === 'playing' 
                    ? 'Game in progress' 
                    : 'Waiting for next round'}
              </button>
            )}

            {/* Bet placed - show locked info while waiting */}
            {hasLocalActiveBet && gameState === 'waiting' && (
              <div className="bet-locked-state">
                <span className="locked-badge">Bet locked for Round #{roundNumber}</span>
                <small>Waiting for game to start...</small>
              </div>
            )}

            {/* Show slips for multiple bets this round */}
            {activeBets.length > 0 && (
              <div className="bet-slips">
                {activeBets.map((b) => (
                  <div key={b.id} className="bet-slip-row">
                    <span>Stake: ${Number(b.amount).toFixed(2)}</span>
                    <button
                      className="cashout-btn"
                      disabled={gameState !== 'playing' || isCashingOut}
                      onClick={() => cashout(b.id)}
                    >
                      {gameState !== 'playing' ? 'Cashout (locked)' : `Cashout $${(Number(b.amount) * multiplier).toFixed(2)}`}
                    </button>
                  </div>
                ))}
              </div>
            )}
          </div>

          <div className="bet-summary">
            <div className="summary-line">
              {hasLocalActiveBet ? (
                <>Current value: ${(betAmount * multiplier).toFixed(2)} at {multiplier.toFixed(2)}x</>
              ) : (
                <>Potential at 2.00x: ${(betAmount * 2.00).toFixed(2)}</>
              )}
            </div>
            <div className={`bet-status ${hasLocalActiveBet ? 'active' : 'idle'}`}>
              {hasLocalActiveBet ? (
                <>Active in Round #{roundNumber} - ${betAmount} locked</>
              ) : isCooldownActive ? (
                <>Cooldown active - Betting will reopen in {cooldownTime}s</>
              ) : (
                <>No active bet - Place bet during waiting phase</>
              )}
            </div>
          </div>
        </div>

      </div>

      <div className="game-history">
        <h3>Recent Results</h3>
        {gameHistory.length === 0 ? (
          <div className="history-empty">
            <p>No game history yet</p>
            <small>Games will appear here after they crash</small>
          </div>
        ) : (
          <div className="history-grid">
            {gameHistory.map((result, index) => {
              const numericResult = Number(result) || 1.0;
              return (
                <div
                  key={index}
                  className={`history-item ${numericResult >= 2 ? 'green' : numericResult >= 1.5 ? 'orange' : 'red'}`}
                >
                  {numericResult.toFixed(2)}x
                </div>
              );
            })}
          </div>
        )}
      </div>

      <div className="bet-history">
        <h3>Your Bet History</h3>
        <div className="bet-history-table">
          <div className="bet-history-header">
            <span>Round</span>
            <span>Stake</span>
            <span>Outcome</span>
            <span>Payout</span>
            <span>Profit</span>
          </div>
          {betHistory.length === 0 && (
            <div className="bet-history-empty">No bets yet</div>
          )}
          {betHistory.map((b, i) => (
            <div className={`bet-history-row ${b.result === 'cashout' ? 'win' : 'loss'}`} key={i}>
              <span>#{b.round || b.game?.round_number}</span>
              <span>${Number(b.amount).toFixed(2)}</span>
              <span>{b.result === 'cashout' ? `${Number(b.cashout_multiplier || b.cashoutMultiplier).toFixed(2)}x` : 'Crashed'}</span>
              <span>${Number(b.payout).toFixed(2)}</span>
              <span className="profit">{`${b.profit >= 0 ? '+' : ''}${Number(b.profit).toFixed(2)}`}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="game-info">
        <h3>How to Play</h3>
        <ul>
          <li>Place your bet during the waiting phase (before takeoff)</li>
          <li>Once bet is placed, your stake is locked for that round</li>
          <li>Watch the multiplier increase in real-time during flight</li>
          <li>Click "Cashout" before the plane crashes to win!</li>
          <li>If the plane crashes before you cashout, you lose your bet</li>
          <li>After each round, you must place a new bet manually</li>
        </ul>
        <div className="backend-info">
          <strong>Real-time Multiplier:</strong> Increases by 0.05x every second during active gameplay
        </div>
      </div>
    </div>
  );
};

export default Aviator;
