import React, { useState, useEffect, useRef } from "react";
import {
  getBalance,
  placeBet,
  cashOut,
  getGameHistory,
  getBetHistory,
  getCurrentGameRaw,
} from "../api";

const Aviator = () => {
  const [balance, setBalance] = useState(0);
  const [multiplier, setMultiplier] = useState(1.0);
  const [gameState, setGameState] = useState("waiting"); // waiting | playing | crashed
  const [crashPoint, setCrashPoint] = useState(null);
  const [gameHistory, setGameHistory] = useState([]);
  const [betHistory, setBetHistory] = useState([]);
  const [betAmount, setBetAmount] = useState(10);
  const [hasBet, setHasBet] = useState(false);
  const [cashoutValue, setCashoutValue] = useState(null);
  const [isAutoCashout, setIsAutoCashout] = useState(false);
  const [autoCashoutValue, setAutoCashoutValue] = useState(2.0);
  const [timeLeft, setTimeLeft] = useState(5); // countdown timer

  const gameInterval = useRef(null);
  const countdownInterval = useRef(null);
  const isRunningRef = useRef(false);

  // âœ… Fetch balance
  const fetchBalance = async () => {
    try {
      const res = await getBalance();
      setBalance(res.balance);
    } catch (err) {
      console.error("Error fetching balance:", err);
    }
  };

  // âœ… Fetch game history
  const fetchGameHistory = async () => {
    try {
      const res = await getGameHistory();
      setGameHistory(res);
    } catch (err) {
      console.error("Error fetching game history:", err);
    }
  };

  // âœ… Fetch bet history
  const fetchBetHistory = async () => {
    try {
      const res = await getBetHistory();
      setBetHistory(res);
    } catch (err) {
      console.error("Error fetching bet history:", err);
    }
  };

  // âœ… Place bet
  const handleBet = async () => {
    try {
      const res = await placeBet(betAmount);
      if (res.success) {
        setHasBet(true);
        fetchBalance();
        fetchBetHistory();
      } else {
        alert("Bet failed");
      }
    } catch (err) {
      console.error("Error placing bet:", err);
    }
  };

  // âœ… Cashout
  const cashout = async () => {
    try {
      const res = await cashOut(multiplier);
      if (res.success) {
        setCashoutValue(multiplier);
        setHasBet(false);
        fetchBalance();
        fetchBetHistory();
      }
    } catch (err) {
      console.error("Error cashing out:", err);
    }
  };

  // âœ… Countdown before each round
  const startCountdown = () => {
    setGameState("waiting");
    setMultiplier(1.0);
    setCrashPoint(null);
    setTimeLeft(5);

    let countdown = 5;
    if (countdownInterval.current) clearInterval(countdownInterval.current);

    countdownInterval.current = setInterval(() => {
      countdown -= 1;
      setTimeLeft(countdown);

      if (countdown <= 0) {
        clearInterval(countdownInterval.current);
        countdownInterval.current = null;
        startGame(); // ðŸš€ after countdown, start aviator
      }
    }, 1000);
  };

  // âœ… Game round
  const startGame = async () => {
    if (isRunningRef.current) return;
    isRunningRef.current = true;

    let crashPoint = null;
    try {
      const g = await getCurrentGameRaw();
      crashPoint = Number(g?.crash_point) || null;
    } catch (e) {
      crashPoint = null;
    }

    // fallback crash point if backend didn't give one
    if (!crashPoint || crashPoint <= 1) {
      crashPoint = 2 + Math.random() * 5;
    }

    setGameState("playing");
    setMultiplier(1.0);

    if (gameInterval.current) clearInterval(gameInterval.current);

    gameInterval.current = setInterval(() => {
      setMultiplier((prev) => {
        const newMult = prev + 0.01;

        if (newMult >= crashPoint) {
          clearInterval(gameInterval.current);
          setMultiplier(crashPoint);
          setCrashPoint(crashPoint);
          setGameState("crashed");
          setGameHistory((prev) => [crashPoint, ...prev.slice(0, 9)]);
          isRunningRef.current = false;

          // After crash, restart countdown after 2s pause
          setTimeout(() => {
            startCountdown();
          }, 2000);

          return crashPoint;
        }

        // Auto cashout
        if (isAutoCashout && newMult >= autoCashoutValue) {
          cashout();
        }

        return newMult;
      });
    }, 50);
  };

  // âœ… On mount
  useEffect(() => {
    fetchBalance();
    fetchGameHistory();
    fetchBetHistory();

    // Start first countdown
    startCountdown();

    return () => {
      if (gameInterval.current) clearInterval(gameInterval.current);
      if (countdownInterval.current) clearInterval(countdownInterval.current);
    };
  }, []);

  return (
    <div>
      <h1>Aviator Game</h1>
      <p>Balance: {balance}</p>
      <p>Status: {gameState}</p>
      {gameState === "waiting" && <p>New round in: {timeLeft}s</p>}
      {gameState === "playing" && <p>Multiplier: {multiplier.toFixed(2)}x</p>}
      {gameState === "crashed" && (
        <p>ðŸ’¥ Crashed at {crashPoint.toFixed(2)}x</p>
      )}

      <div>
        <input
          type="number"
          value={betAmount}
          onChange={(e) => setBetAmount(Number(e.target.value))}
        />
        {!hasBet ? (
          <button onClick={handleBet}>Bet {betAmount}</button>
        ) : (
          <button onClick={cashout}>Cashout</button>
        )}
      </div>

      <div>
        <label>
          Auto Cashout:
          <input
            type="checkbox"
            checked={isAutoCashout}
            onChange={() => setIsAutoCashout(!isAutoCashout)}
          />
        </label>
        <input
          type="number"
          step="0.1"
          value={autoCashoutValue}
          onChange={(e) => setAutoCashoutValue(Number(e.target.value))}
        />
      </div>

      <h2>Game History</h2>
      <ul>
        {gameHistory.map((h, i) => (
          <li key={i}>{h.toFixed(2)}x</li>
        ))}
      </ul>

      <h2>Bet History</h2>
      <ul>
        {betHistory.map((b, i) => (
          <li key={i}>
            {b.amount} at {b.multiplier}x
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Aviator;
