import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import './Aviator.css';

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || 'http://127.0.0.1:8000';

export default function Aviator() {
  const [multiplier, setMultiplier] = useState(1.0);
  const [gameState, setGameState] = useState('waiting');
  const [betAmount, setBetAmount] = useState('');
  const [isBetPlaced, setIsBetPlaced] = useState(false);
  const [isBetQueued, setIsBetQueued] = useState(false);
  const [isAutoBet, setIsAutoBet] = useState(false);
  const [isAutoCashout, setIsAutoCashout] = useState(false);
  const [autoCashoutValue, setAutoCashoutValue] = useState('');
  const [hasCashedOut, setHasCashedOut] = useState(false);
  const [lockedBetAmount, setLockedBetAmount] = useState(null);
  const ws = useRef(null);

  useEffect(() => {
    ws.current = new WebSocket('ws://127.0.0.1:8000/ws/aviator/');

    ws.current.onmessage = (e) => {
      const data = JSON.parse(e.data);
      if (data.type === 'multiplier_update') {
        setMultiplier(data.multiplier);
      } else if (data.type === 'game_state') {
        setGameState(data.state);
        if (data.state === 'waiting') {
          setMultiplier(1.0);
          setIsBetPlaced(false);
          setHasCashedOut(false);
          setLockedBetAmount(null);
          if (isBetQueued || isAutoBet) {
            placeBet();
            setIsBetQueued(false);
          }
        }
      }
    };

    return () => ws.current.close();
  }, [isBetQueued, isAutoBet]);

  useEffect(() => {
    if (isBetPlaced && isAutoCashout && autoCashoutValue && multiplier >= parseFloat(autoCashoutValue) && !hasCashedOut) {
      cashout();
    }
  }, [multiplier, isBetPlaced, isAutoCashout, autoCashoutValue, hasCashedOut]);

  const placeBet = async () => {
    if (!betAmount) return;
    try {
      await axios.post(`${API_BASE_URL}/place-bet/`, { amount: betAmount });
      setLockedBetAmount(betAmount);
      setIsBetPlaced(true);
      setHasCashedOut(false);
    } catch (error) {
      console.error('Error placing bet:', error);
    }
  };

  const cashout = async () => {
    try {
      await axios.post(`${API_BASE_URL}/cashout/`, { multiplier });
      setHasCashedOut(true);
      setIsBetPlaced(false);
    } catch (error) {
      console.error('Error cashing out:', error);
    }
  };

  const addChip = (amount) => {
    setBetAmount((prev) => (Number(prev) + amount).toString());
  };

  return (
    <div className="aviator-container">
      <div className="game-area">
        <AnimatePresence>
          {gameState === 'playing' && (
            <motion.div
              key="plane"
              initial={{ x: -100, y: 50 }}
              animate={{ x: 300, y: -150 }}
              transition={{ duration: 5, ease: 'linear' }}
              className="plane"
            >
              ✈️
            </motion.div>
          )}
        </AnimatePresence>
        <div className="multiplier-display">{multiplier.toFixed(2)}x</div>
      </div>

      <div className="betting-panel">
        <input
          type="number"
          value={betAmount}
          disabled={isBetPlaced} // lock input after betting
          onChange={(e) => setBetAmount(e.target.value)}
          placeholder="Enter bet"
        />
        <div className="chips">
          {[10, 50, 100].map((chip) => (
            <button key={chip} disabled={isBetPlaced} onClick={() => addChip(chip)} className="chip">+{chip}</button>
          ))}
        </div>

        {!isBetPlaced && gameState === 'waiting' && (
          <button onClick={placeBet} className="bet-btn">Place Bet</button>
        )}

        {isBetPlaced && gameState === 'playing' && !hasCashedOut && (
          <button onClick={cashout} className="cashout-btn">
            Cashout ${(lockedBetAmount * multiplier).toFixed(2)}
          </button>
        )}

        {hasCashedOut && <div className="status">You cashed out!</div>}
      </div>

      <div className="controls">
        <label>
          <input type="checkbox" checked={isAutoBet} onChange={(e) => setIsAutoBet(e.target.checked)} /> Auto Bet
        </label>
        <label>
          <input type="checkbox" checked={isAutoCashout} onChange={(e) => setIsAutoCashout(e.target.checked)} /> Auto Cashout
          {isAutoCashout && (
            <input
              type="number"
              value={autoCashoutValue}
              onChange={(e) => setAutoCashoutValue(e.target.value)}
              placeholder="At multiplier"
            />
          )}
        </label>
      </div>
    </div>
  );
}
