import React, { useState, useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import axios from 'axios';
import './Aviator.css';

const Aviator = () => {
  const [gameState, setGameState] = useState('waiting'); // waiting, playing, crashed
  const [multiplier, setMultiplier] = useState(1.00);
  const [betAmount, setBetAmount] = useState(10);
  const [balance, setBalance] = useState(1000);
  const [isBetPlaced, setIsBetPlaced] = useState(false);
  const [gameHistory, setGameHistory] = useState([]);
  const [timeLeft, setTimeLeft] = useState(5);
  const [crashPoint, setCrashPoint] = useState(0);
  const [isAutoCashout, setIsAutoCashout] = useState(false);
  const [autoCashoutValue, setAutoCashoutValue] = useState(2.00);
  const [roundNumber, setRoundNumber] = useState(0);
  const [isBetQueued, setIsBetQueued] = useState(false);
  const [queuedBetAmount, setQueuedBetAmount] = useState(10);
  const [betHistory, setBetHistory] = useState([]);
  const [currentGame, setCurrentGame] = useState(null);
  const [error, setError] = useState(null);
  const [currentOdds, setCurrentOdds] = useState(null);
  
  const gameInterval = useRef(null);
  const countdownInterval = useRef(null);
  const gameCheckInterval = useRef(null);

  // API calls
  const fetchBalance = async () => {
    try {
      const response = await axios.get('/api/aviator/balance/');
      setBalance(response.data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
      // Use demo balance if not authenticated
      setBalance(1000);
    }
  };

  const fetchCurrentOdds = async () => {
    try {
      const response = await axios.get('/api/aviator/current-odds/');
      setCurrentOdds(response.data);
    } catch (error) {
      console.error('Error fetching current odds:', error);
      setCurrentOdds(null);
    }
  };

  const fetchCurrentGame = async () => {
    try {
      const response = await axios.get('/api/aviator/current-game/');
      const game = response.data;
      setCurrentGame(game);
      setRoundNumber(game.round_number);
      
      if (game.status === 'waiting') {
        setGameState('waiting');
        setMultiplier(1.00);
        // Calculate time left based on when game was created
        const created = new Date(game.created_at);
        const now = new Date();
        const elapsed = Math.floor((now - created) / 1000);
        const remaining = Math.max(0, 5 - elapsed);
        setTimeLeft(remaining);
        
        // Set the crash point from backend (this is predetermined!)
        setCrashPoint(game.crash_point);
      } else if (game.status === 'active') {
        setGameState('playing');
        setMultiplier(1.00);
        // Calculate current multiplier based on when game started
        const started = new Date(game.started_at);
        const now = new Date();
        const elapsed = Math.floor((now - started) / 1000);
        const currentMultiplier = 1.00 + (elapsed * 0.01);
        setMultiplier(Math.min(currentMultiplier, game.crash_point));
      } else if (game.status === 'crashed') {
        setGameState('crashed');
        setCrashPoint(game.crash_point);
        setMultiplier(game.crash_point);
      }
    } catch (error) {
      console.error('Error fetching current game:', error);
    }
  };

  const fetchGameHistory = async () => {
    try {
      const response = await axios.get('/api/aviator/game-history/');
      setGameHistory(response.data.map(game => game.crash_point));
    } catch (error) {
      console.error('Error fetching game history:', error);
    }
  };

  const fetchBetHistory = async () => {
    try {
      const response = await axios.get('/api/aviator/history/');
      setBetHistory(response.data);
    } catch (error) {
      console.error('Error fetching bet history:', error);
    }
  };

  // Place bet
  const placeBet = async () => {
    if (balance >= betAmount && !isBetPlaced && gameState === 'waiting') {
      try {
        const response = await axios.post('/api/aviator/place-bet/', {
          amount: betAmount
        });
        
        setBalance(response.data.balance);
        setIsBetPlaced(true);
        setError(null);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to place bet');
        console.error('Error placing bet:', error);
      }
    }
  };

  // Cashout
  const cashout = async () => {
    if (isBetPlaced && gameState === 'playing') {
      try {
        const response = await axios.post('/api/aviator/cashout/', {
          round_number: roundNumber,
          multiplier: multiplier
        });
        
        setBalance(response.data.balance);
        setIsBetPlaced(false);
        
        // Add to history
        setBetHistory(prev => [response.data.bet, ...prev.slice(0, 19)]);
        setError(null);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to cashout');
        console.error('Error cashing out:', error);
      }
    }
  };

  // Start new game - now uses backend crash points
  const startNewGame = () => {
    if (currentGame && currentGame.crash_point) {
      // Use the predetermined crash point from backend
      setCrashPoint(currentGame.crash_point);
      setGameState('waiting');
      setMultiplier(1.00);
      setTimeLeft(5);
      
      // Start countdown
      countdownInterval.current = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(countdownInterval.current);
            startGame(currentGame.crash_point);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      // Fallback - wait for backend to provide game data
      setTimeLeft(5);
      countdownInterval.current = setInterval(() => {
        setTimeLeft(prev => {
          if (prev <= 1) {
            clearInterval(countdownInterval.current);
            // Try to fetch game data again
            fetchCurrentGame();
            return 5;
          }
          return prev - 1;
        });
      }, 1000);
    }
  };

  // Start the actual game using backend crash point
  const startGame = (backendCrashPoint) => {
    setGameState('playing');
    setMultiplier(1.00);
    
    // Use the exact crash point from backend
    const targetCrashPoint = backendCrashPoint;
    
    gameInterval.current = setInterval(() => {
      setMultiplier(prev => {
        const newMultiplier = prev + 0.01;
        
        if (newMultiplier >= targetCrashPoint) {
          // Game crashed at the predetermined point
          clearInterval(gameInterval.current);
          setGameState('crashed');
          
          // Add to history
          setGameHistory(prev => [targetCrashPoint, ...prev.slice(0, 9)]);
          
          // Handle bet loss
          if (isBetPlaced) {
            setIsBetPlaced(false);
            // Add to bet history
            setBetHistory(prev => [
              {
                round: roundNumber,
                amount: betAmount,
                result: 'crash',
                cashoutMultiplier: null,
                payout: 0,
                profit: -betAmount
              },
              ...prev.slice(0, 19)
            ]);
          }
          
          return targetCrashPoint;
        }
        
        // Check auto cashout
        if (isAutoCashout && newMultiplier >= autoCashoutValue) {
          cashout();
        }
        
        return newMultiplier;
      });
    }, 50);
  };

  // Double bet
  const doubleBet = () => {
    if (balance >= betAmount * 2) {
      setBetAmount(prev => prev * 2);
    }
  };

  // Half bet
  const halfBet = () => {
    setBetAmount(prev => Math.max(1, Math.floor(prev / 2)));
  };

  // Reset bet
  const resetBet = () => {
    setBetAmount(10);
  };

  // Quick chips and queue controls
  const addChip = (amount) => {
    setBetAmount(prev => {
      const next = Math.max(1, prev + amount);
      return Math.min(next, balance);
    });
  };
  
  const setMaxBet = () => {
    setBetAmount(Math.max(1, Math.floor(balance)));
  };
  
  const clearBet = () => {
    setBetAmount(1);
  };
  
  const queueBetForNextRound = () => {
    if (!isBetPlaced && balance >= betAmount) {
      setQueuedBetAmount(betAmount);
      setIsBetQueued(true);
    }
  };
  
  const cancelQueuedBet = () => {
    setIsBetQueued(false);
  };

  // Start new game when current one ends
  useEffect(() => {
    if (gameState === 'crashed') {
      const timer = setTimeout(() => {
        startNewGame();
      }, 3000);
      return () => clearTimeout(timer);
    }
  }, [gameState]);

  // Cleanup intervals
  useEffect(() => {
    return () => {
      if (gameInterval.current) clearInterval(gameInterval.current);
      if (countdownInterval.current) clearInterval(countdownInterval.current);
      if (gameCheckInterval.current) clearInterval(gameCheckInterval.current);
    };
  }, []);

  // Initialize and periodic updates
  useEffect(() => {
    fetchBalance();
    fetchCurrentOdds();
    fetchCurrentGame();
    fetchGameHistory();
    fetchBetHistory();
    
    // Check for game updates every 2 seconds
    gameCheckInterval.current = setInterval(() => {
      fetchCurrentGame();
    }, 2000);
    
    // Initial game start
    const timer = setTimeout(() => {
      startNewGame();
    }, 1000);
    
    return () => clearTimeout(timer);
  }, []);

  return (
    <div className="aviator-container">
      <div className="aviator-header">
        <h1>Aviator</h1>
        <div className="balance-display">
          Balance: ${balance.toFixed(2)}
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
        </div>
      )}

      <div className="game-area">
        <div className="multiplier-display">
          <AnimatePresence mode="wait">
            {gameState === 'waiting' && (
              <motion.div
                key="waiting"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="waiting-state"
              >
                <h2>Next game in {timeLeft}s</h2>
                <div className="countdown-circle">
                  <span>{timeLeft}</span>
                </div>
                <div className="round-info">Round #{roundNumber}</div>
                {currentOdds && (
                  <div className="odds-info">
                    <small>Using: {currentOdds.name}</small>
                    {currentOdds.use_custom_points && (
                      <small>Custom Points: {currentOdds.custom_crash_points?.split(',').length || 0}</small>
                    )}
                  </div>
                )}
              </motion.div>
            )}
            
            {gameState === 'playing' && (
              <motion.div
                key="playing"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="playing-state"
              >
                <h2 className="multiplier-text">
                  {multiplier.toFixed(2)}x
                </h2>
                <motion.div
                  className="airplane"
                  animate={{
                    y: [0, -20, 0],
                    rotate: [0, 5, -5, 0]
                  }}
                  transition={{
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                >
                  
                </motion.div>
                <div className="round-info">Round #{roundNumber}</div>
              </motion.div>
            )}
            
            {gameState === 'crashed' && (
              <motion.div
                key="crashed"
                initial={{ opacity: 0, scale: 0.8 }}
                animate={{ opacity: 1, scale: 1 }}
                exit={{ opacity: 0, scale: 0.8 }}
                className="crashed-state"
              >
                <h2 className="crash-text">CRASHED AT {crashPoint.toFixed(2)}x</h2>
                <div className="crash-animation">
                  
                </div>
                <div className="round-info">Round #{roundNumber}</div>
                <div className="backend-info">
                  <small>Predetermined crash point from backend</small>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </div>

        <div className="betting-controls">
          <div className="bet-amount-controls">
            <button onClick={halfBet} className="bet-btn"></button>
            <button onClick={resetBet} className="bet-btn">Reset</button>
            <button onClick={doubleBet} className="bet-btn">2</button>
          </div>
          
          <div className="bet-input">
            <input
              type="number"
              value={betAmount}
              onChange={(e) => setBetAmount(Math.max(1, parseInt(e.target.value) || 1))}
              min="1"
              max={balance}
              className="bet-amount-input"
            />
          </div>

          <div className="quick-chips">
            <button className="chip" onClick={() => addChip(1)}>+1</button>
            <button className="chip" onClick={() => addChip(5)}>+5</button>
            <button className="chip" onClick={() => addChip(10)}>+10</button>
            <button className="chip" onClick={() => addChip(50)}>+50</button>
            <button className="chip" onClick={() => addChip(100)}>+100</button>
            <button className="chip" onClick={setMaxBet}>Max</button>
            <button className="chip outline" onClick={clearBet}>Clear</button>
          </div>
          
          <div className="action-buttons">
            {!isBetPlaced && gameState === 'waiting' && (
              <button onClick={placeBet} className="place-bet-btn">
                Place Bet ${betAmount}
              </button>
            )}
            
            {isBetPlaced && gameState === 'playing' && (
              <button onClick={cashout} className="cashout-btn">
                Cashout ${(betAmount * multiplier).toFixed(2)}
              </button>
            )}

            {!isBetPlaced && gameState !== 'waiting' && (
              isBetQueued ? (
                <div className="queued-controls">
                  <span className="queued-badge">Queued ${queuedBetAmount} for next round</span>
                  <button onClick={cancelQueuedBet} className="queue-cancel-btn">Cancel</button>
                </div>
              ) : (
                <button onClick={queueBetForNextRound} className="queue-bet-btn">
                  Queue ${betAmount} for next round
                </button>
              )
            )}
          </div>

          <div className="bet-summary">
            <div className="summary-line">
              Potential at {autoCashoutValue.toFixed(2)}x: ${ (betAmount * autoCashoutValue).toFixed(2) }
            </div>
            <div className={`bet-status ${isBetPlaced ? 'active' : isBetQueued ? 'queued' : 'idle'}`}>
              {isBetPlaced ? (
                <>Active in Round {roundNumber}</>
              ) : isBetQueued ? (
                <>Queued ${queuedBetAmount} for next round</>
              ) : (
                <>No active bet</>
              )}
            </div>
          </div>
        </div>

        <div className="auto-cashout-section">
          <label className="auto-cashout-label">
            <input
              type="checkbox"
              checked={isAutoCashout}
              onChange={(e) => setIsAutoCashout(e.target.checked)}
            />
            Auto Cashout at
          </label>
          <input
            type="number"
            value={autoCashoutValue}
            onChange={(e) => setAutoCashoutValue(parseFloat(e.target.value) || 2.00)}
            min="1.01"
            step="0.01"
            className="auto-cashout-input"
          />
          <span>x</span>
        </div>
      </div>

      <div className="game-history">
        <h3>Recent Results</h3>
        <div className="history-grid">
          {gameHistory.map((result, index) => (
            <div
              key={index}
              className={`history-item ${result >= 2 ? 'green' : result >= 1.5 ? 'orange' : 'red'}`}
            >
              {result.toFixed(2)}x
            </div>
          ))}
        </div>
      </div>

      <div className="bet-history">
        <h3>Your Bet History</h3>
        <div className="bet-history-table">
          <div className="bet-history-header">
            <span>Round</span>
            <span>Stake</span>
            <span>Outcome</span>
            <span>Payout</span>
            <span>Profit</span>
          </div>
          {betHistory.length === 0 && (
            <div className="bet-history-empty">No bets yet</div>
          )}
          {betHistory.map((b, i) => (
            <div className={`bet-history-row ${b.result === 'cashout' ? 'win' : 'loss'}`} key={i}>
              <span>#{b.round || b.game?.round_number}</span>
              <span>${Number(b.amount).toFixed(2)}</span>
              <span>{b.result === 'cashout' ? `${Number(b.cashout_multiplier || b.cashoutMultiplier).toFixed(2)}x` : 'Crashed'}</span>
              <span>${Number(b.payout).toFixed(2)}</span>
              <span className="profit">{`${b.profit >= 0 ? '+' : ''}${Number(b.profit).toFixed(2)}`}</span>
            </div>
          ))}
        </div>
      </div>

      <div className="game-info">
        <h3>How to Play</h3>
        <ul>
          <li>Place your bet before the game starts</li>
          <li>Watch the multiplier increase from 1.00x</li>
          <li>Cash out before it crashes to win!</li>
          <li>Set auto cashout for automatic wins</li>
        </ul>
        <div className="backend-info">
          <strong>Backend Controlled:</strong> Crash points are predetermined from your custom odds configuration
          {currentOdds && (
            <div className="odds-details">
              <p><strong>Current Odds Set:</strong> {currentOdds.name}</p>
              <p><strong>Early Crash:</strong> {currentOdds.early_crash_probability}% ({currentOdds.early_crash_min}x - {currentOdds.early_crash_max}x)</p>
              <p><strong>High Crash:</strong> {currentOdds.high_crash_min}x - {currentOdds.high_crash_max}x</p>
              {currentOdds.use_custom_points && (
                <p><strong>Custom Points:</strong> {currentOdds.custom_crash_points?.split(',').length || 0} predetermined crash points</p>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Aviator;
