import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';

function Home() {
  const [upcomingMatches, setUpcomingMatches] = useState([]);
  const [recentResults, setRecentResults] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [matchesResponse, resultsResponse] = await Promise.all([
          axios.get('/api/upcoming-matches/'),
          axios.get('/api/recent-results/')
        ]);
        
        setUpcomingMatches(matchesResponse.data);
        setRecentResults(resultsResponse.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  // Default games with realistic team names when API is not available
  const defaultUpcomingMatches = [
    {
      id: 1,
      home_team: { name: 'Manchester United' },
      away_team: { name: 'Liverpool FC' },
      start_time: new Date(Date.now() + 10 * 60000).toISOString()
    },
    {
      id: 2,
      home_team: { name: 'Arsenal' },
      away_team: { name: 'Chelsea' },
      start_time: new Date(Date.now() + 20 * 60000).toISOString()
    },
    {
      id: 3,
      home_team: { name: 'Barcelona' },
      away_team: { name: 'Real Madrid' },
      start_time: new Date(Date.now() + 30 * 60000).toISOString()
    },
    {
      id: 4,
      home_team: { name: 'Bayern Munich' },
      away_team: { name: 'Paris Saint-Germain' },
      start_time: new Date(Date.now() + 40 * 60000).toISOString()
    },
    {
      id: 5,
      home_team: { name: 'Juventus' },
      away_team: { name: 'AC Milan' },
      start_time: new Date(Date.now() + 50 * 60000).toISOString()
    },
    {
      id: 6,
      home_team: { name: 'Manchester City' },
      away_team: { name: 'Tottenham Hotspur' },
      start_time: new Date(Date.now() + 60 * 60000).toISOString()
    }
  ];

  const defaultRecentResults = [
    {
      id: 1,
      home_team: { name: 'Inter Milan' },
      away_team: { name: 'Borussia Dortmund' },
      result: '2-1',
      start_time: new Date(Date.now() - 60 * 60000).toISOString()
    },
    {
      id: 2,
      home_team: { name: 'Atletico Madrid' },
      away_team: { name: 'Sevilla' },
      result: '1-1',
      start_time: new Date(Date.now() - 120 * 60000).toISOString()
    },
    {
      id: 3,
      home_team: { name: 'Porto' },
      away_team: { name: 'Benfica' },
      result: '3-0',
      start_time: new Date(Date.now() - 180 * 60000).toISOString()
    },
    {
      id: 4,
      home_team: { name: 'Ajax' },
      away_team: { name: 'PSV Eindhoven' },
      result: '2-2',
      start_time: new Date(Date.now() - 240 * 60000).toISOString()
    },
    {
      id: 5,
      home_team: { name: 'Manchester United' },
      away_team: { name: 'Arsenal' },
      result: '1-0',
      start_time: new Date(Date.now() - 300 * 60000).toISOString()
    }
  ];

  // Use API data if available, otherwise use defaults
  const displayUpcomingMatches = upcomingMatches.length > 0 ? upcomingMatches : defaultUpcomingMatches;
  const displayRecentResults = recentResults.length > 0 ? recentResults : defaultRecentResults;

  return (
    <div className="home">
      <h1>Welcome to Virtual Betting</h1>
      <p>Place bets on virtual football matches and win big!</p>
      
      <div className="home-sections">
        <div className="section">
          <h2>Upcoming Matches</h2>
          <div className="matches-grid">
            {upcomingMatches.slice(0, 6).map(match => (
              <div key={match.id} className="match-card">
                <div className="match-teams">
                  <span className="team">{match.home_team.name}</span>
                  <span className="vs">vs</span>
                  <span className="team">{match.away_team.name}</span>
                </div>
                <div className="match-time">
                  {new Date(match.start_time).toLocaleString()}
                </div>
                <div className="bet-options">
                  <button className="bet-btn">Home Win (2.0)</button>
                  <button className="bet-btn">Draw (3.0)</button>
                  <button className="bet-btn">Away Win (2.5)</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="section">
          <h2>Recent Results</h2>
          <div className="results-list">
            {recentResults.slice(0, 5).map(match => (
              <div key={match.id} className="result-item">
                <div className="result-teams">
                  <span className="team">{match.home_team.name}</span>
                  <span className="score">{match.result}</span>
                  <span className="team">{match.away_team.name}</span>
                </div>
                <div className="result-time">
                  {new Date(match.start_time).toLocaleDateString()}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
