import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

function Navbar() {
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  // Close dropdown and mobile menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (isDropdownOpen && !event.target.closest('.navbar-dropdown')) {
        setIsDropdownOpen(false);
      }
      if (isMobileMenuOpen && !event.target.closest('.navbar')) {
        setIsMobileMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [isDropdownOpen, isMobileMenuOpen]);

  return (
    <nav className="navbar">
      <div className="navbar-brand">
        <Link to="/" className="navbar-logo">
          Virtual Betting
        </Link>
      </div>
      
      {/* Mobile Menu Toggle */}
      <button 
        className={`mobile-menu-toggle ${isMobileMenuOpen ? 'active' : ''}`}
        onClick={toggleMobileMenu}
        aria-label="Toggle mobile menu"
      >
        <span className="hamburger-line"></span>
        <span className="hamburger-line"></span>
        <span className="hamburger-line"></span>
      </button>

      <div className={`navbar-menu ${isMobileMenuOpen ? 'mobile-open' : ''}`}>
        <Link to="/" className="navbar-item" onClick={() => setIsMobileMenuOpen(false)}>Home</Link>
        
        {/* Dropdown Menu */}
        <div className="navbar-dropdown">
          <button 
            className="navbar-dropdown-btn"
            onClick={toggleDropdown}
          >
            Games â–¼
          </button>
          {isDropdownOpen && (
            <div className="dropdown-menu">
              <Link to="/matches" className="dropdown-item" onClick={() => setIsMobileMenuOpen(false)}>Upcoming Events</Link>
              <Link to="/results" className="dropdown-item" onClick={() => setIsMobileMenuOpen(false)}>Recent Results</Link>
            </div>
          )}
        </div>
        
        <Link to="/bet-slip" className="navbar-item" onClick={() => setIsMobileMenuOpen(false)}>Bet Slip</Link>
        <Link to="/login" className="navbar-item" onClick={() => setIsMobileMenuOpen(false)}>Login</Link>
      </div>
    </nav>
  );
}

export default Navbar;
