import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './Aviator.css';

const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://127.0.0.1:8000';

const Aviator = () => {
  // Game state from backend
  const [gameState, setGameState] = useState('waiting');
  const [multiplier, setMultiplier] = useState(1.00);
  const [betAmount, setBetAmount] = useState(10);
  const [balance, setBalance] = useState(1000);
  const [isBetPlaced, setIsBetPlaced] = useState(false);
  const [gameHistory, setGameHistory] = useState([]);
  const [timeLeft, setTimeLeft] = useState(10);
  const [crashPoint, setCrashPoint] = useState(0);
  const [isAutoCashout, setIsAutoCashout] = useState(false);
  const [autoCashoutValue, setAutoCashoutValue] = useState(2.00);
  const [roundNumber, setRoundNumber] = useState(0);
  const [isBetQueued, setIsBetQueued] = useState(false);
  const [queuedBetAmount, setQueuedBetAmount] = useState(10);
  const [betHistory, setBetHistory] = useState([]);
  const [currentGame, setCurrentGame] = useState(null);
  const [error, setError] = useState(null);
  const [currentOdds, setCurrentOdds] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [gamePhase, setGamePhase] = useState('waiting');

  // Real-time polling intervals
  const realtimeInterval = useRef(null);
  const gameCheckInterval = useRef(null);
  const isRunningRef = useRef(false);
  const gameStateRef = useRef(gameState);

  // Real-time game state polling - gets current multiplier from backend
  const fetchRealtimeGameState = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/aviator/realtime/`);
      const data = response.data;

      // Update game state from backend
      setRoundNumber(data.round_number);
      setGameState(data.status);
      setCrashPoint(Number(data.crash_point));
      setMultiplier(data.current_multiplier);
      setTimeLeft(data.time_remaining);
      setGamePhase(data.phase);

      // Handle game transitions
      if (data.game_crashed) {
        // Game just crashed, show result briefly
        setGameState('crashed');
        setMultiplier(Number(data.crashed_at));

        // Reset bet state
        setIsBetPlaced(false);

        // Add to game history
        setGameHistory(prev => [Number(data.crashed_at), ...prev.slice(0, 9)]);

        // Start new round after brief delay
        setTimeout(() => {
          setGameState('waiting');
          setMultiplier(1.00);
        }, 2000);
      }

      // Handle auto-cashout
      if (isAutoCashout && data.phase === 'playing' && data.current_multiplier >= autoCashoutValue) {
        cashout();
      }

    } catch (error) {
      console.error('Error fetching real-time game state:', error);
    }
  };

  // Fetch initial game data
  const fetchCurrentGame = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/api/aviator/sync/`);
      const data = response.data;

      setCurrentGame(data.game);
      setCurrentOdds(data.odds);
      setRoundNumber(data.game.round_number);
      setCrashPoint(Number(data.game.crash_point));
      setGameState(data.game.status);
      setMultiplier(data.current_multiplier);
      setTimeLeft(data.timing.time_remaining);

      // Set game phase based on status
      if (data.game.status === 'waiting') {
        setGamePhase('waiting');
      } else if (data.game.status === 'active') {
        setGamePhase('playing');
      } else {
        setGamePhase('crashed');
      }

      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching current game:', error);
      setError('Failed to load game data');
      setIsLoading(false);
    }
  };

  // Fetch user balance
  const fetchBalance = async () => {
    try {
      const token = localStorage.getItem('access_token');
      if (!token) return;

      const response = await axios.get(`${API_BASE_URL}/api/aviator/balance/`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setBalance(response.data.balance);
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  // Place bet
  const placeBet = async () => {
    try {
      const token = localStorage.getItem('access_token');
      if (!token) {
        setError('Please log in to place a bet');
        return;
      }

      const response = await axios.post(`${API_BASE_URL}/api/aviator/place-bet/`, {
        amount: betAmount
      }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.data.success) {
        setIsBetPlaced(true);
        setBalance(response.data.new_balance);
        setError(null);
      }
    } catch (error) {
      console.error('Error placing bet:', error);
      setError(error.response?.data?.error || 'Failed to place bet');
    }
  };

  // Cashout
  const cashout = async () => {
    try {
      const token = localStorage.getItem('access_token');
      if (!token) return;

      const response = await axios.post(`${API_BASE_URL}/api/aviator/cashout/`, {}, {
        headers: { 'Authorization': `Bearer ${token}` }
      });

      if (response.data.success) {
        setIsBetPlaced(false);
        setBalance(response.data.new_balance);
        setBetHistory(prev => [{
          amount: response.data.bet_amount,
          multiplier: multiplier,
          profit: response.data.profit
        }, ...prev.slice(0, 9)]);
      }
    } catch (error) {
      console.error('Error cashing out:', error);
    }
  };

  // Queue bet for next round
  const queueBet = () => {
    if (gameState === 'waiting') {
      setIsBetQueued(true);
      setQueuedBetAmount(betAmount);
    }
  };

  // Cancel queued bet
  const cancelQueuedBet = () => {
    setIsBetQueued(false);
    setQueuedBetAmount(0);
  };

  // Handle bet amount change
  const handleBetAmountChange = (e) => {
    const value = Number(e.target.value);
    if (value > 0 && value <= balance) {
      setBetAmount(value);
    }
  };

  // Handle auto-cashout change
  const handleAutoCashoutChange = (e) => {
    const value = Number(e.target.value);
    if (value >= 1.00) {
      setAutoCashoutValue(value);
    }
  };

  // Start real-time polling
  useEffect(() => {
    // Initial fetch
    fetchCurrentGame();
    fetchBalance();

    // Start real-time polling (every 100ms for smooth updates)
    realtimeInterval.current = setInterval(fetchRealtimeGameState, 100);

    // Game state check (every 1 second)
    gameCheckInterval.current = setInterval(() => {
      if (gameState === 'waiting' || gameState === 'active') {
        fetchCurrentGame();
      }
    }, 1000);

    // Cleanup
    return () => {
      if (realtimeInterval.current) {
        clearInterval(realtimeInterval.current);
      }
      if (gameCheckInterval.current) {
        clearInterval(gameCheckInterval.current);
      }
    };
  }, []);

  // Update game state ref
  useEffect(() => {
    gameStateRef.current = gameState;
  }, [gameState]);

  // Handle queued bet when game starts
  useEffect(() => {
    if (gameState === 'active' && isBetQueued && gamePhase === 'starting') {
      setBetAmount(queuedBetAmount);
      setIsBetQueued(false);
      placeBet();
    }
  }, [gameState, gamePhase, isBetQueued]);

  if (isLoading) {
    return (
      <div className="aviator-container">
        <div className="loading">Loading Aviator Game...</div>
      </div>
    );
  }

  return (
    <div className="aviator-container">
      <div className="game-header">
        <h1>Aviator Game</h1>
        <div className="round-info">
          <span>Round #{roundNumber}</span>
          <span className="balance">Balance: ${balance}</span>
        </div>
      </div>

      {error && (
        <div className="error-message">
          {error}
          <button onClick={() => setError(null)}>Ã—</button>
        </div>
      )}

      <div className="game-display">
        <div className="multiplier-display">
          <div className="multiplier-value">
            {gameState === 'crashed' ? `${multiplier.toFixed(2)}x` : `${multiplier.toFixed(2)}x`}
          </div>
          <div className="game-status">
            {gameState === 'waiting' && `Next round in ${timeLeft.toFixed(1)}s`}
            {gameState === 'active' && gamePhase === 'playing' && `Flying to ${crashPoint.toFixed(2)}x`}
            {gameState === 'crashed' && 'CRASHED!'}
          </div>
        </div>

        <div className="crash-point">
          Crash Point: {crashPoint.toFixed(2)}x
        </div>
      </div>

      <div className="betting-controls">
        <div className="bet-input">
          <label>Bet Amount:</label>
          <input
            type="number"
            value={betAmount}
            onChange={handleBetAmountChange}
            min="1"
            max={balance}
            disabled={isBetPlaced || gameState !== 'waiting'}
          />
        </div>

        <div className="bet-buttons">
          {!isBetPlaced ? (
            <>
              <button
                className="place-bet-btn"
                onClick={placeBet}
                disabled={gameState !== 'waiting' || betAmount > balance}
              >
                Place Bet
              </button>
              {gameState === 'waiting' && (
                <button
                  className="queue-bet-btn"
                  onClick={queueBet}
                  disabled={betAmount > balance}
                >
                  Queue Bet
                </button>
              )}
            </>
          ) : (
            <button
              className="cashout-btn"
              onClick={cashout}
              disabled={gameState !== 'active'}
            >
              Cashout at {multiplier.toFixed(2)}x
            </button>
          )}
        </div>

        {isBetQueued && (
          <div className="queued-bet">
            <span>Bet queued: ${queuedBetAmount}</span>
            <button onClick={cancelQueuedBet}>Cancel</button>
          </div>
        )}
      </div>

      <div className="auto-cashout">
        <label>
          <input
            type="checkbox"
            checked={isAutoCashout}
            onChange={(e) => setIsAutoCashout(e.target.checked)}
          />
          Auto Cashout at:
        </label>
        <input
          type="number"
          value={autoCashoutValue}
          onChange={handleAutoCashoutChange}
          min="1.00"
          step="0.10"
          disabled={!isAutoCashout}
        />
        x
      </div>

      <div className="game-info">
        <div className="game-history">
          <h3>Recent Crashes</h3>
          <div className="history-list">
            {gameHistory.map((crash, index) => (
              <span key={index} className="crash-value">
                {crash.toFixed(2)}x
              </span>
            ))}
          </div>
        </div>

        <div className="bet-history">
          <h3>Recent Bets</h3>
          <div className="history-list">
            {betHistory.map((bet, index) => (
              <div key={index} className="bet-result">
                <span>${bet.amount}</span>
                <span>{bet.multiplier.toFixed(2)}x</span>
                <span className={bet.profit >= 0 ? 'profit' : 'loss'}>
                  {bet.profit >= 0 ? '+' : ''}${bet.profit}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      <div className="debug-info">
        <div>Game State: {gameState}</div>
        <div>Phase: {gamePhase}</div>
        <div>Time Left: {timeLeft.toFixed(1)}s</div>
        <div>Multiplier: {multiplier.toFixed(3)}x</div>
      </div>
    </div>
  );
};

export default Aviator;
